/// Here we define the stacks which should be tested.
namespace kodo
{

    // Put dummy layers and tests classes in an anonymous namespace
    // to avoid violations of ODF (one-definition-rule) in other
    // translation units
    namespace
    {


        template<class Field>
        class vandermonde_stack : public
            vandermonde_matrix<
            finite_field_math<typename fifi::default_field<Field>::type,
            finite_field_info<Field,
            final_layer
            > > >
        {
        public:
            using factory = basic_factory<vandermonde_stack>;
        };

        template<class Field>
        class systematic_vandermonde_stack : public
            systematic_vandermonde_matrix<
            finite_field_math<typename fifi::default_field<Field>::type,
            finite_field_info<Field,
            final_layer
            > > >
        {
        public:
            using factory = basic_factory<systematic_vandermonde_stack>;
        };
    }
}

namespace
{

    /// Tests:
    ///   - construct_matrix(uint32_t)
    template<class Generator>
    struct api_construct_matrix
    {
        typedef typename Generator::factory factory_type;
        typedef typename Generator::field_type field_type;
        typedef typename Generator::value_type value_type;

        api_construct_matrix(uint32_t max_symbols, uint32_t max_symbol_size)
            : m_factory(max_symbols, max_symbol_size)
            { }

        void run(uint32_t symbols, const value_type *test_values)
            {
                auto matrix = m_factory.construct_matrix(symbols);

                for(uint32_t i = 0; i < field_type::order - 1; ++i)
                {
                    for(uint32_t j = 0; j < symbols; ++j)
                    {
                        value_type test = test_values[i * symbols + j];
                        value_type actual = matrix->element(i, j);

                        ASSERT_EQ(test, actual);
                    }
                }
            }

    private:

        // The factory
        factory_type m_factory;

    };

}


// Array with results for the Vandermonde matrix with 10
// source symbols in non-systematic form
extern const fifi::binary8::value_type test_rs8_10_nonsystematic[];
extern const fifi::binary8::value_type test_rs8_10_systematic[];

TEST(TestVandermondeMatrix, test_matrix_values)
{

    typedef fifi::binary8 field_type;

    uint32_t symbols = 10;
    uint32_t symbol_size = rand_symbol_size();

    {
        typedef api_construct_matrix<
            kodo::vandermonde_stack<field_type> > test;

        test runner(symbols, symbol_size);
        runner.run(symbols, test_rs8_10_nonsystematic);
    }

    {
        typedef api_construct_matrix<
            kodo::systematic_vandermonde_stack<field_type> > test;

        test runner(symbols, symbol_size);
        runner.run(symbols, test_rs8_10_systematic);
    }

}


